import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import RadioGroup from "@material-ui/core/RadioGroup";
import Radio from "@material-ui/core/Radio";

import './styles.css'

interface RowData {
    rowInfo: {
        id?: number,
        col1?: string,
        col2?: string,
        col3?: string,
    }
}

interface UserInfo {
    email: string,
    age: number,
    gender: string
} 

export const FormComponent = (props: RowData) => {

    const handleUserInfoChange = (e: any, key: string) => {
        const updatedUserInfo = { ... userData, key : e.target.value};
        updateUserData(updatedUserInfo);
    }



    const minAge = 0;
    const maxAge = 100;

    const userInfo = {
        'email' : '', 
        'age': minAge, 
        'gender': ''
    };
    const [userData, updateUserData] = useState<UserInfo>(userInfo);

    return (
        <>
        <TextField id="email-input" name="email" 
        value = {userData.email} helperText="Please enter your email address" type="text" required onChange = {(e) => (handleUserInfoChange(e, 'email'))}/>
        <br/>
        <TextField id = "age-input" type = 'number' inputProps = 
        {{minAge,  maxAge}} required name = "age" helperText="Please enter your age" onChange = {(e) => (handleUserInfoChange(e, 'age'))}/> 
        <br/> <br/>
        <FormControl>
        <FormLabel id="demo-row-radio-buttons-group-label">Gender</FormLabel>
        <RadioGroup
        row
        aria-labelledby="demo-row-radio-buttons-group-label"
        name="row-radio-buttons-group"
        >
        <FormControlLabel value="female" control={<Radio />} label="Female" />
        <FormControlLabel value="male" control={<Radio />} label="Male" />
        <FormControlLabel value="other" control={<Radio />} label="Other" />
        </RadioGroup>
        </FormControl>
        </>
    )
}
