import React, { useState } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

import { rows, columns } from '../constants';
import { FormComponent } from './form'
import './styles.css';

export const TableComponent : React.FC = () => {

  const [rowSelected, updateRowSelected] = useState<object>({})
  return (
    <>
    {Object.keys(rowSelected).length == 0 && <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {columns.map(item => (
                <TableCell align = "center">{item.headerName}</TableCell>
            ))
            }
          </TableRow>
        </TableHead>
        <TableBody className = 'pointer'>
          {rows.map((row) => (
            <TableRow
              key={row.id}
              onClick = {() => updateRowSelected({...rowSelected,
                 ...row})}
              selected = {true}
              hover = {true}
            >
              <TableCell component="th" scope="row">
                {row.col1}
              </TableCell>
              <TableCell align="center">{row.col2}</TableCell>
              <TableCell align="center">{row.col3}</TableCell>
              <TableCell align="center">{row.col4}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>}

      {Object.keys(rowSelected).length != 0 &&  
      <FormComponent rowInfo = {rowSelected}/>}
      </>
  );
}
